import React, {Component} from "react";
import { Navigation, Pagination, Autoplay, Scrollbar} from 'swiper';
import { Swiper, SwiperSlide } from 'swiper/react';
import slideImg1 from '../images/slideImg1.jpg';
import slideImg2 from '../images/slideImg2.jpg';
import slideImg3 from '../images/slideImg3.jpg';
import slideImg4 from '../images/slideImg4.jpg';


import 'swiper/css/bundle';
import 'swiper/css/navigation';
import 'swiper/css/pagination';
import 'swiper/css/autoplay';
import 'swiper/css/scrollbar';
import './Carousel.css';


class Carousel extends Component {

    render() {

        const slideImagesNames = ["slideImg1", "slideImg2", "slideImg3"]

        return (

            <Swiper 
            modules={[Navigation, Pagination, Autoplay, Scrollbar]}
            spaceBetween={0}
            slidesperview={3}
            navigation={true}
            autoplay={{
                delay: 4000,
                disableOnInteraction: false,
            }}
            pagination={{ clickable: true }}
            scrollbar={{ draggable: true }}
            onSwiper={(swiper) => console.log(swiper)}
            onSlideChange={() =>  console.log('slide change')}
            >
            {slideImagesNames.map(image=> <SwiperSlide><img src={require(`../images/${image}.jpg`)}></img></SwiperSlide>)}


            </Swiper>


        );
    }
}

export default Carousel;
